DROP PROCEDURE IF EXISTS `createEssay`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `createEssay`(IN s_corpus_id int, IN s_user_id int, IN s_user_name VARCHAR(120), IN s_essay_title VARCHAR(120), IN s_essay_content text, IN s_essay_create_time TIMESTAMP, OUT r_result int )
BEGIN DECLARE insert_count int DEFAULT 0; DECLARE insert_essay_id int DEFAULT 0; START TRANSACTION;
UPDATE T_Corpus
SET essay_number = essay_number + 1
WHERE id = s_corpus_id;
  SELECT row_count() INTO insert_count; IF(insert_count != 1) THEN ROLLBACK;
  SET r_result = -1; ELSE
  UPDATE T_User
  SET user_essay_number = user_essay_number + 1 WHERE id = s_user_id;
  SELECT row_count() INTO insert_count; IF(insert_count != 1) THEN ROLLBACK;
  SET r_result = -1; ELSE
  INSERT INTO T_Essay(user_id, user_name, essay_title, essay_content, corpus_id)
  VALUES(s_user_id,
         s_user_name,
         s_essay_title,
         s_essay_content,
         s_corpus_id);
  SELECT row_count() INTO insert_count;
  SELECT LAST_INSERT_ID() INTO insert_essay_id; IF(insert_count != 1) THEN ROLLBACK;
  SET r_result = -1; ELSE COMMIT;
  SET r_result = 1; END IF; END IF; END IF; END
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for deleteEssay
-- ----------------------------
DROP PROCEDURE IF EXISTS `deleteEssay`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `deleteEssay`(IN s_corpus_id INT, IN s_essay_id INT, IN s_user_id INT, OUT r_result INT)
BEGIN DECLARE delete_count INT DEFAULT 0 ; DECLARE select_essay_word_number INT DEFAULT 0 ; START TRANSACTION ;
UPDATE T_Corpus
SET essay_number = essay_number - 1
WHERE id = s_corpus_id AND essay_number >= 0 ;
  SELECT row_count() INTO delete_count ; IF (delete_count != 1) THEN ROLLBACK ;
  SET r_result = - 1 ; ELSE
  UPDATE T_User
  SET user_essay_number = user_essay_number - 1 WHERE id = s_user_id ;
  SELECT row_count() INTO delete_count ; IF (delete_count != 1) THEN ROLLBACK ;
  SET r_result = - 2 ; ELSE
  SELECT essay_word_number INTO select_essay_word_number
  FROM T_Essay WHERE id = s_essay_id ;
  UPDATE T_User
  SET user_words_number = user_words_number - select_essay_word_number WHERE id = s_user_id
  AND user_words_number >= 0 ;
  SELECT row_count() INTO delete_count ;
  UPDATE T_Corpus
  SET essay_word_number = essay_word_number - select_essay_word_number WHERE id = s_corpus_id
  AND essay_word_number > 0;
  DELETE FROM T_Label WHERE user_id = s_user_id AND essay_id = s_essay_id;
  SELECT row_count() INTO delete_count ; IF (delete_count != 1) THEN ROLLBACK ;
  SET r_result = - 5 ; ELSE
  DELETE FROM T_Essay WHERE id = s_essay_id ;
  SELECT row_count() INTO delete_count ; IF (delete_count != 1) THEN ROLLBACK ;
  SET r_result = - 6 ; ELSE COMMIT ;
  SET r_result = 1 ; END IF ; END IF ; END IF ; END IF ; END
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for updateEssay
-- ----------------------------
DROP PROCEDURE IF EXISTS `updateEssay`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `updateEssay`(IN s_corpus_id INT, IN s_essay_id INT, IN s_user_id INT, IN s_essay_title VARCHAR(120), IN s_essay_content longtext, IN s_essay_update_time TIMESTAMP, IN s_essay_image_path VARCHAR(180), IN s_essay_old_word_number INT , IN s_essay_new_word_number INT, IN s_is_hot INT, OUT r_result INT)
BEGIN DECLARE update_count INT DEFAULT 0 ; START TRANSACTION ;
UPDATE T_Corpus
SET essay_word_number = essay_word_number + s_essay_new_word_number - s_essay_old_word_number
WHERE id = s_corpus_id
  AND essay_word_number >= 0;
  SELECT row_count() INTO update_count ; IF (update_count != 1) THEN ROLLBACK ;
  SET r_result = - 1 ; ELSE
  UPDATE T_User
  SET user_words_number = user_words_number + s_essay_new_word_number - s_essay_old_word_number WHERE id = s_user_id
  AND user_words_number >= 0 ;
  SELECT row_count() INTO update_count ; IF (update_count != 1) THEN ROLLBACK ;
  SET r_result = - 1 ; ELSE
  UPDATE T_Essay
  SET essay_title = s_essay_title,
      essay_content = s_essay_content,
      essay_image_path = s_essay_image_path,
      essay_time = s_essay_update_time,
      essay_word_number = s_essay_new_word_number,
      is_hot = s_is_hot WHERE id = s_essay_id ; IF (update_count != 1) THEN ROLLBACK ;
  SET r_result = - 1 ; ELSE COMMIT ;
  SET r_result = 1 ; END IF ; END IF ; END IF ; END
;;
DELIMITER ;
